mapper.select:
SELECT ifnull(afn_fulfillable_quantity,0)+ifnull(afn_inbound_working_quantity,0)+ ifnull(afn_inbound_shipped_quantity,0)+ifnull(afn_inbound_receiving_quantity,0)+ case when rr.id is null then ifnull(afn_reserved_quantity,0) else ifnull(rr.reserved_fc_processing,0)+ifnull(rr.reserved_fc_transfers,0) end quantity, ifnull(afn_fulfillable_quantity,0)+ case when rr.id is null then ifnull(afn_reserved_quantity,0) else ifnull(rr.reserved_fc_processing,0)+ifnull(rr.reserved_fc_transfers,0) end localquantity, v.sku,v.marketname,v.marketplaceid,v.sysavgsales,v.amazonauthid, v.openDate,v.sum15,v.summonth,v.sumweek,v.msku,v.groupid,v.shopid,v.sumseven, v.statusname,v.statuscolor, p.avgsales,p.needship,p.needshipfba,p.needpurchase,p.ship_min_cycle_sale,p.short_time, p.needpurchase,p.salesday,p.shipday,p.delivery_cycle, r.afn_fulfillable_quantity, r.afn_inbound_working_quantity, r.afn_unsellable_quantity, r.afn_inbound_shipped_quantity,r.afn_inbound_receiving_quantity,r.afn_reserved_quantity, r.afn_total_quantity,r.afn_researching_quantity,r.afn_reserved_future_supply, rr.reserved_fc_processing,rr.reserved_fc_transfers,rr.reserved_qty,rr.reserved_customerorders, i.subnum,i.aftersalesday,ifnull(i.amount,ifnull(p.needship,0)) amount, ifnull(i.amount,0) reallyamount FROM ( select info.sku sku, auth.id amazonauthid, auth.shop_id shopid, auth.groupid, case when mk.region='EU' then 'EU' ELSE mk.marketplaceId END marketplaceid, case when mk.region='EU' then '欧洲' ELSE mk.name END marketname, ifnull(o.msku,info.sku) msku, sum(t.sales_week) sumweek, sum(t.sales_seven) sumseven, sum(t.sales_month) summonth, sum(t.sales_fifteen) sum15, min(info.openDate) openDate, sum(t.avgsales) sysavgsales, max(d.name) statusname, max(d.color) statuscolor, max(mk.findex) findex FROM t_product_info info left join t_product_in_opt o on o.pid=info.id left join t_product_info_status_define d on d.id=o.status LEFT JOIN t_product_in_order t on t.pid = info.id left join t_marketplace mk on info.marketplaceid=mk.marketplaceid left join t_amazon_auth auth on auth.id = info.amazonAuthId where auth.disable = FALSE and (o.disable is null or o.disable=0) and info.invalid=0 and info.isparent=0 and auth.shop_id=#{shopid,jdbcType=CHAR}
and auth.groupid=#{groupid,jdbcType=CHAR}
AND IFNULL(o.msku,info.sku) =#{msku,jdbcType=CHAR} group by sku,case when mk.region='EU' then 'EU' ELSE mk.marketplaceId END ) v LEFT JOIN t_amz_product_sales_plan p ON p.sku=v.sku AND p.marketplaceid=v.marketplaceid AND p.amazonauthid=v.amazonauthid and p.shopid=v.shopid and p.groupid=v.groupid and p.msku=v.msku LEFT JOIN t_inventory_report r ON r.sku=v.sku AND r.marketplaceid=v.marketplaceid AND r.amazonAuthId=v.amazonauthid LEFT JOIN t_inventory_reserved_report rr on v.sku=rr.sku and v.marketplaceid=rr.marketplaceid and v.amazonAuthId=rr.amazonAuthId LEFT JOIN t_erp_market_priority pri on pri.marketplaceid=v.marketplaceid and pri.groupid=v.groupid LEFT JOIN (select shopid,groupid,amazonauthid,case when m.region='EU' then 'EU' else i.marketplaceid end marketplaceid, sku,sum(case when m.region='EU' then 2 else 1 end) subnum,max(aftersalesday) aftersalesday,sum(amount) amount from t_amz_product_sales_plan_ship_item i left join t_marketplace m on m.marketplaceid=i.marketplaceid where shopid=#{shopid,jdbcType=CHAR} and groupid=#{groupid,jdbcType=CHAR} and warehouseid=#{warehouseid,jdbcType=CHAR} group by shopid,groupid,amazonauthid,case when m.region='EU' then 'EU' else i.marketplaceid end,sku ) i on i.sku=v.sku and i.amazonauthid=v.amazonauthid and i.shopid=v.shopid and i.groupid=v.groupid and i.marketplaceid=v.marketplaceid order by ifnull(pri.priority,v.findex)
